import React, { useState } from 'react';
import "./App.css";

function App() {
  // Estados para manejar los datos ingresados
  const [urlMateria, setUrlMateria] = useState('');
  const [grupos, setGrupos] = useState('');
  const [correos, setCorreos] = useState('');

  // Función que se ejecuta cuando se hace clic en el botón de búsqueda
  const handleBusqueda = () => {
    // Lógica para manejar la búsqueda
    console.log('URL de la Materia:', urlMateria);
    console.log('Grupos:', grupos);
    console.log('Correos Electrónicos:', correos);
    // Aquí puedes agregar la lógica para procesar los datos
  };

  return (
    <div className="App">
      <h1>Monitoreo de Cupos de Materias</h1>

      {/* Entrada de URL de la Materia */}
      <div>
        <label>URL de la Materia:</label>
        <input 
          type="text" 
          value={urlMateria}
          onChange={(e) => setUrlMateria(e.target.value)}
          placeholder="Ingrese la URL de la materia"
        />
      </div>

      {/* Entrada de Grupos */}
      <div>
        <label>Grupos a Monitorear (separados por comas):</label>
        <input 
          type="text" 
          value={grupos}
          onChange={(e) => setGrupos(e.target.value)}
          placeholder="Ej: 020-84,020-81"
        />
      </div>

      {/* Entrada de Correos Electrónicos de Destinatarios */}
      <div>
        <label>Correos Electrónicos (separados por comas):</label>
        <input 
          type="text" 
          value={correos}
          onChange={(e) => setCorreos(e.target.value)}
          placeholder="Ej: email1@gmail.com, email2@gmail.com"
        />
      </div>

      {/* Botón para Iniciar la Búsqueda */}
      <div>
        <button onClick={handleBusqueda}>Iniciar Búsqueda</button>
      </div>
    </div>
  );
}

export default App;
