import React, { useState } from "react";
import "../BoxUpdate.css"; // Estilos para personalizar la tabla

const BoxUpdate = () => {
  // Estado inicial con materias, grupos y estado activo/inactivo
  const [materias, setMaterias] = useState([
    { id: 1, nombre: "Matemáticas", grupo: "Grupo 1", activo: true },
    { id: 2, nombre: "Física", grupo: ["Grupo 2", "Grupo 3"], activo: false },
    { id: 3, nombre: "Química", grupo: "Grupo 3", activo: true },
  ]);

  // Función para actualizar el grupo de una materia
  const handleGrupoChange = (id, nuevoGrupo) => {
    const materiasActualizadas = materias.map((materia) =>
      materia.id === id ? { ...materia, grupo: nuevoGrupo } : materia
    );
    setMaterias(materiasActualizadas);
  };

  // Función para cambiar el estado activo/inactivo
  const handleActivoChange = (id) => {
    const materiasActualizadas = materias.map((materia) =>
      materia.id === id ? { ...materia, activo: !materia.activo } : materia
    );
    setMaterias(materiasActualizadas);
  };

  return (
    <div className="materia-table-container">
      <h2>Lista de Materias y Grupos</h2>
      <table className="materia-table">
        <thead>
          <tr>
            <th>Materia</th>
            <th>Grupos</th>
            <th>En Busqueda</th>
          </tr>
        </thead>
        <tbody>
          {materias.map((materia) => (
            <tr key={materia.id}>
              <td>{materia.nombre}</td>
              <td>
                <input
                  type="text"
                  value={materia.grupo}
                  onChange={(e) =>
                    handleGrupoChange(materia.id, e.target.value)
                  }
                />
              </td>
              <td>
                <label className="switch">
                  <input
                    type="checkbox"
                    checked={materia.activo}
                    onChange={() => handleActivoChange(materia.id)}
                  />
                  <span className="slider round"></span>
                </label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BoxUpdate;
